import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Profile from './components/Profile';
import Navbar from './components/Navbar';
import { isAuthenticated, getUserIdFromJwt, getToken } from './services/auth.service';
import './styles.css';

const App = () => {
  const [authenticated, setAuthenticated] = useState(false);
  const [username, setUsername] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is already authenticated
    const checkAuth = async () => {
      if (isAuthenticated()) {
        try {
          const token = getToken();
          const userId = getUserIdFromJwt(token);
          
          // Fetch username if needed
          // This would typically come from the JWT claims or a separate API call
          // For now, we'll just use the user ID as a placeholder
          setUsername(userId);
          setAuthenticated(true);
        } catch (error) {
          console.error('Authentication error:', error);
          setAuthenticated(false);
        }
      } else {
        setAuthenticated(false);
      }
      
      setLoading(false);
    };
    
    checkAuth();
  }, []);

  const handleLogin = (token) => {
    // Extract user info from token if needed
    const userId = getUserIdFromJwt(token);
    setUsername(userId);
    setAuthenticated(true);
  };

  if (loading) {
    return <div className="loading-screen">Loading...</div>;
  }

  return (
    <div className="app">
      <Navbar username={username} />
      
      <main className="main-content">
        {authenticated ? (
          <Profile />
        ) : (
          <Login onLogin={handleLogin} />
        )}
      </main>
      
      <footer className="footer">
        <div className="footer-content">
          <p>GraphQL Profile Project Â© {new Date().getFullYear()}</p>
        </div>
      </footer>
    </div>
  );
};

export default App;
